<template>
  <div>
    <div style="margin-top:60px ">
    <h1 style="color: #000000"><b>로그인</b></h1>
  </div>
    <div class="logintotalForm">
      <div id="loginForm">
        <div class="loginForm-group">
          <label for="email" ></label>
          <input
            class="inputForm"
            type="text"
            id="email"
            style="border-radius: 5px; height: 50px"
            v-model="email"
            placeholder="이메일"
            size="40"
          />
        </div>
        <div class="loginForm-group">
          <label for="password"></label>
          <input
            class="inputForm"
            type="password"
            id="password"
            style="border-radius: 5px; height: 50px" 
            v-model="password"
            placeholder="비밀번호"
            size="40"
            
          />
        </div>
        <br>
        <div
          style="display: flex; justify-content: center; align-items: center"
        >
          <a :href="`${apiUrl}/oauth2/authorization/google`">
            <img
              src="data:image/png;base64,"
              alt="구글"
              width="50"
            />
          </a>
          &nbsp; &nbsp; &nbsp;
          <a :href="`${apiUrl}/oauth2/authorization/kakao`">
            <img
              src="data:image/png;base64,"
              alt="카카오 로그인"
              width="50"
            />
          </a>
        </div>
        <br/>
        <div style="  display: flex;
        align-items: center;
        flex-direction: column;
        justify-content: center;
        width: 100%; "> 
          <p class="findPassword" @click="findPassword" style="cursor:pointer;"><b>비밀번호</b>를 잊으셨나요?</p>
          <button class="login" @click="login" size="40">로그인</button>
          <p class="toJoin" @click="toJoin" style="cursor:pointer;" >처음이신가요? <b>회원가입</b></p>
        </div>
      </div>
    </div>
  </div>
</template>
<script>
import { apiUrl } from "../../../config/config.js";
import router from "@/router";
export default {
  data() {
    return {
      apiUrl,
      email: "",
      password: "",
    };
  },

  methods: {
    toJoin() {
      router.push({ name: "Join" });
    },
    findPassword(){
      router.push({ name: 'FindPassword' });
    },
    login() {
      let user = {
        email: this.email,
        password: this.password,
      };
      const frm = new FormData();
      frm.append("email", user.email);
      frm.append("password", user.password);
      this.$store.dispatch("loginStore/setLoginUser", frm);
    },
  },
  created() {
    const report = this.$route.query.report;
    if (report) {

      alert("신고당한 유저입니다. 관리자에게 문의 해주세요.");
    }
  },
};
</script>
